            );

            if (notification) {
                res.status(204).json({ message: 'Read Status changed successfully' });
            } else {
                res.status(404).json({ message: 'Notification not found' });
            }
        } catch (error) {
            res.status(500).json({ message: 'Internal server error' });
        }
    }
));

export default router;

